INSERT INTO `rankup_schedule` VALUES (1,2,'창립기념일',61,'2012-06-07','2012-06-07',NULL,NULL,'\"귀하가 있어 올해도 창립기념일을 무사히 맞이하였습니다. 감사합니다.\"\r\n \r\n\"회사의 생일입니다. 축하해주세요.\"\r\n \r\n\"창립기념일 기념\"','yes',NULL),(2,2,'상반기 워크샵',63,'2012-06-21','2012-06-22',NULL,NULL,'상반기 워크샵 일정안내\r\n\r\n주제별요청: 세미나. 도보여행, 등산 트레킹, 여행패키지,등 테마별 여행디자인.\r\n\r\n숙소: 경남통영-비치케슬 최고급 펜션리조트 \r\n\r\n일정: 2012-03-21 ~ 2012-03-22 (1박 2일)\r\n\r\n\r\n\r\n1일차-\r\n\r\n \r\n\r\n09:30 경- 지정하는곳 - 집결(인원점검)\r\n\r\n10:00 경- 각지역 출발\r\n\r\n12:00 경- 리조트 도착 중식 (멍게비빕밥) 특식\r\n\r\n13:00 경- 통영비치케슬 도착 (통영시 평림동)\r\n\r\n13:30 경- 방배정 (3층룸 15-25평)룸 배정 (단체팀 인원고려 배정)\r\n\r\n14:00 경- 세미나실 세미나 (2시간)진행\r\n\r\n16:00 경- 세미나 종료\r\n\r\n17:00 경- 석식 (회정식만찬 리조트 10분거리 식당예약진행)등 자유선택\r\n\r\n18:30 경- 석식종료후 투숙 자유시간\r\n\r\n  \r\n\r\n \r\n\r\n2일차-\r\n\r\n \r\n\r\n08:00 경-리조트 조식; (부추조개탕) 특식\r\n\r\n09:30 경- 버스 탑승 : 통영지역 관광\r\n\r\n  \r\n\r\n \r\n\r\n코스: \r\n\r\n향토역사관-세병관-이순신공원-충렬사- 중식-해저터널-통영대교-통영해양관광공원-달아공원-통영수산과학관-중식(영빈관2호점)통영케이블카-통영전통공예관-남망산조각공원\r\n\r\n  \r\n\r\n(코스는 현지상황 및 동선상,시간상 변동이 가능하며.기사와 협의진행 바랍니다, 당코스는 통영시청 추천코스입니다)\r\n\r\n@ (입장료가 있는곳은 단체할인을 현장에서 적용받고 정산하여 입장합니다)\r\n\r\n(입장료 하단참조) \r\n\r\n05:00 경-통영출발\r\n\r\n07:30 경-집결지 도착 해산','yes',NULL),(3,2,'상반기 정기휴가',65,'2012-06-30','2012-06-30',NULL,NULL,'상반기 정기휴가 안내\r\n\r\n당직근무자 (이지훈 대리)제외 모두 해당일 휴무입니다.\r\n\r\n즐거운 연휴보내세요~','yes',NULL),(4,2,'2011 EMC/EMI /ESD 솔루션 및 설계동향',62,'2012-06-12','2012-06-12',NULL,NULL,'디지털 제품의 융,복합화가 가속화하면서 고속신호전송용 인터페이스 기술 적용이 나날이 늘고 있다. 그러나 이 같은 경향으로 인해 제품 내 또는 제품 간 전기적인 충돌이나 사고도 꾸준히 발생한다. 일례로 차량 급발진 사고도 이 같은 전기적인 장치의 전자파 간섭(EMI)이 원인일 것으로 추정할 만큼 사회적인 이슈로 부각되고 있습니다.\r\n\r\n최근에 EMC/EMI/ESD 보호에 대한 기대가 놓아짐에 따라 여러 연구기관에서 관련 연구가 활발히 \r\n이어지고 있다 . 현재 KAIST(테라랩)에서도 전자파를 발생시키는 고속신호 및 전원에 대한 연구를 기반으로 반도체 PCB, 커넥터, 케이블 등 다양한 분야에서 연구를 하고 있으며 그 외 중견,중소기업의 연구기관에서도 활발한 연구가 이뤄지고 있습니다.\r\n\r\n\r\n시장 경쟁력 강화를 위한 정보교류의 장에 귀하를 초대합니다.','yes',NULL),(5,2,'3200개 판매 기념 이벤트',63,'2012-06-01','2012-06-01',NULL,NULL,'솔루션10%할인+모바일+sms3200건지급','yes',NULL),(6,2,'회사소개솔루션 출시',64,'2012-06-12','2012-06-12',NULL,NULL,'업종별 솔루션 1탄\r\n\r\n기업/제품홍보 솔루션 출시~','yes',NULL),(7,2,'이벤트 SMS발송 처리',64,'2012-06-15','2012-06-15',NULL,NULL,'사이트 전고객 SMS 홍보문구 발송처리','yes',NULL);
